    def get_IQF(self):
        vna = hp.HP8753E()
        self._I, self._Q, self._F = vna.get_IQF_single_meas()
        sleep = vna.get_span()/vna.get_IFBW()
        for i in range(0,int(sleep)):
            self.progressBar.setValue(i) 
        time.sleep(2)
        return
        
    def crf(self):
        vna = hp.HP8753E()
        myfile = Path(str(vna._path)+"Run_"+str(self.num)+".h5")
        if (myfile.is_file()==True):
            print('File exists already!')
            self.num = self.num + 1
            vna.create_run_file(num=self.num, i=self._I,q=self._Q,f=self._F)
        else:
            vna.create_run_file(num=self.num, i=self._I,q=self._Q,f=self._F)
            print('File does not exist') 
        self.progressBar.reset()
        return

    def check(self):
        vna = hp.HP8753E()
        _, msg = vna.check_status()
        msg = QtWidgets.QMessageBox()
        msg.setIcon(QtWidgets.QMessageBox.Information)
        msg.setWindowTitle('Message')
        msg.setText(msg)
        msg.exec_()
        return

